import React, { useState, useEffect } from 'react';
import { socket } from '../socket';

const RoomManagement = ({ onJoinRoom, isTeacher }) => {
  const [roomId, setRoomId] = useState('');
  const [participants, setParticipants] = useState([]);
  const [userName, setUserName] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    socket.on('participantJoined', (participant) => {
      setParticipants(prev => [...prev, participant]);
    });

    socket.on('participantLeft', (participantId) => {
      setParticipants(prev => prev.filter(p => p.id !== participantId));
    });

    socket.on('roomError', (message) => {
      setError(message);
    });

    return () => {
      socket.off('participantJoined');
      socket.off('participantLeft');
      socket.off('roomError');
    };
  }, []);

  const handleCreateRoom = () => {
    if (!userName.trim()) {
      setError('Please enter your name');
      return;
    }
    socket.emit('createRoom', userName, (response) => {
      if (response.success) {
        setRoomId(response.roomId);
        onJoinRoom(response.roomId, userName, true);
      } else {
        setError(response.message);
      }
    });
  };

  const handleJoinRoom = () => {
    if (!userName.trim()) {
      setError('Please enter your name');
      return;
    }
    if (!roomId.trim()) {
      setError('Please enter a room code');
      return;
    }
    socket.emit('joinRoom', { roomId, userName }, (response) => {
      if (response.success) {
        onJoinRoom(roomId, userName, false);
      } else {
        setError(response.message);
      }
    });
  };

  return (
    <div className="room-management">
      <div className="room-container">
        <div className="room-header">
          <h2>Welcome to Presidential Decision Maker</h2>
          <p>Join your classroom to begin the activity</p>
        </div>

        {error && <div className="error-message">{error}</div>}

        <div className="room-options">
          <input
            type="text"
            className="room-input"
            placeholder="Enter your name"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />

          {isTeacher ? (
            <button 
              className="room-button"
              onClick={handleCreateRoom}
              disabled={!userName.trim()}
            >
              Create Classroom
            </button>
          ) : (
            <>
              <input
                type="text"
                className="room-input"
                placeholder="Enter room code"
                value={roomId}
                onChange={(e) => setRoomId(e.target.value)}
              />
              <button 
                className="room-button"
                onClick={handleJoinRoom}
                disabled={!userName.trim() || !roomId.trim()}
              >
                Join Classroom
              </button>
            </>
          )}
        </div>

        {participants.length > 0 && (
          <div className="participants-list">
            <h3>Participants</h3>
            {participants.map(participant => (
              <div key={participant.id} className="participant-item">
                <div 
                  className="participant-cursor"
                  style={{ backgroundColor: participant.color }}
                />
                <span>{participant.name}</span>
                {participant.isTeacher && <span>(Teacher)</span>}
              </div>
            ))}
          </div>
        )}

        {participants.length > 0 && !isTeacher && (
          <p className="waiting-message">
            Waiting for teacher to start the activity...
          </p>
        )}
      </div>
    </div>
  );
};

export default RoomManagement;
